    { id: "managers", label: "‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå", icon: "üë®‚Äçüíº" },
    { id: "settings", label: "‡∏¢‡∏∑‡∏°-‡∏Ñ‡∏∑‡∏ô", icon: "‚öôÔ∏è" },
    { id: "records", label: "‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏Ñ‡∏á‡πÄ‡∏´‡∏•‡∏∑‡∏≠", icon: "üìä" },
    { id: "items", label: "‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏¢‡∏¥‡∏°‡∏Ñ‡∏∑‡∏ô", icon: "üìë" },
  ];

  return (
    <div className="sidebar">
      {/* Logo */}
      <div className="sidebar-logo">
        <div className="logo-icon">E</div>
        <div className="logo-text">
          <div className="logo-main">Eqborrow</div>
          <div className="logo-sub">E-PAYMENT ADMIN</div>
        </div>
      </div>

      {/* Menu Items */}
      <nav className="sidebar-menu">
        {menuItems.map((item) => (
          <button
            key={item.id}
            className={`menu-item ${activeMenu === item.id ? "active" : ""}`}
            onClick={() => {
              setActiveMenu(item.id);
            }}
          >
            <span className="menu-icon">{item.icon}</span>
            <span className="menu-label">{item.label}</span>
          </button>
        ))}
      </nav>

      {/* User Profile */}
      {user && (
        <div
          style={{
            padding: "15px 20px",
            borderTop: "1px solid rgba(255,255,255,0.1)",
            display: "flex",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <img
            src={user.profileImage || "https://via.placeholder.com/40"}
            alt="Profile"
            style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              objectFit: "cover",
              border: "2px solid #ff8000",
            }}
          />
          <div style={{ display: "flex", flexDirection: "column" }}>
            <span
